package com.sean.rest.utils;

// TODO: Auto-generated Javadoc
/**
 * The Class State.
 * 
 * @author seanmenezes
 * 
 *         Holds State coordinates
 */
public class State {

	/** The name. */
	private String name;

	/** The north. */
	private double NORTH;

	/** The south. */
	private double SOUTH;

	/** The east. */
	private double EAST;

	/** The west. */
	private double WEST;

	/**
	 * Gets the name.
	 * 
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * Sets the name.
	 * 
	 * @param name
	 *            the new name
	 */
	public void setName(String name) {
		this.name = name;
	}

	/**
	 * Gets the north.
	 * 
	 * @return the north
	 */
	public double getNORTH() {
		return NORTH;
	}

	/**
	 * Sets the north.
	 * 
	 * @param nORTH
	 *            the new north
	 */
	public void setNORTH(double nORTH) {
		NORTH = nORTH;
	}

	/**
	 * Gets the south.
	 * 
	 * @return the south
	 */
	public double getSOUTH() {
		return SOUTH;
	}

	/**
	 * Sets the south.
	 * 
	 * @param sOUTH
	 *            the new south
	 */
	public void setSOUTH(double sOUTH) {
		SOUTH = sOUTH;
	}

	/**
	 * Gets the east.
	 * 
	 * @return the east
	 */
	public double getEAST() {
		return EAST;
	}

	/**
	 * Sets the east.
	 * 
	 * @param eAST
	 *            the new east
	 */
	public void setEAST(double eAST) {
		EAST = eAST;
	}

	/**
	 * Gets the west.
	 * 
	 * @return the west
	 */
	public double getWEST() {
		return WEST;
	}

	/**
	 * Sets the west.
	 * 
	 * @param wEST
	 *            the new west
	 */
	public void setWEST(double wEST) {
		WEST = wEST;
	}

}
